import React from "react";

export default function New({ news }) {
  return (
    <div>
      <img
        src={`https://ipfs.infura.io/ipfs/${news.image}`}
        alt="image"
      />
      <div>
        <div>
          <h3>{news.title}</h3>
          <p >
            {news.date}
          </p>
        </div>
      </div>

      <div>
        <div>
          Author: {news?.author?.slice(0, 12)}...
        </div>
      </div>
      <p>{news.description}</p>
    </div>
  );
}
