export default function Writing() {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [coverImage, setCoverImage] = useState("");

    const handleSubmit = async () => {
        if (
          title === "" ||
          description === "" ||
          category === "" ||
          location === "" ||
          coverImage === ""
        ) {
          alert("All the fields must be filled!");
          return;
        }
    };
}
