import { useState } from 'react';
import { ethers } from 'ethers'
import { useNavigate } from 'react-router-dom';
import News from './artifacts/contracts/News.sol/News.json'

// Update with the contract address logged out to the CLI when it was deployed 
const newAddress = "0x0165878A594ca255338adfa4d48449f69242Eb8F"
const navigate = useNavigate()

function App() {
  // store greeting in local state
  const [New, setNewValue] = useState()

  // request access to the user's MetaMask account
  async function requestAccount() {
    await window.ethereum.request({ method: 'eth_requestAccounts' });
  }

  // call the smart contract, read the current greeting value
  async function fetchNew() {
    if (typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum)
      const contract = new ethers.Contract(newAddress, News.abi, provider)
      try {
        const data = await contract.getAllNews()
        console.log('data: ', data)
      } catch (err) {
        console.log("Error: ", err)
      }
    }    
  }

  function handleOnClick() {
    navigate('<route here>');
  }

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={fetchNew}>Fetch New</button>
        <Link to="/writing">
          <button onClick={handleOnClick()}>Write a New</button>
        </Link>
        <input onChange={e => setNewValue(e.target.value)} placeholder="Set new" />
      </header>
    </div>
  );
}

export default App;
