import React, { useEffect, useState } from "react";
import getContract from "./utilities/getContract";
import { Link } from "react-router-dom";
import UnvotedNew from "./components/UnvotedNew";

export default function NewsPage() {
    const [news, setNews] = useState([]);
    const [vote, setVote] = useState("");

    const getUrlValue = () => {
        let vars = {};
        window.location.href.replace(
            /[?&]+([^=&]+)=([^&]*)/gi,
            function (m, key, value) {
                vars[key] = value;
            }
        );
        return vars;
    };

    const getNews = async () => {
        try {
            const contract = await getContract();
            let newsId = getUrlValue()["id"];
            const singleNew = await contract.getNew(newsId);

            const formattedNew = {
                id: singleNew[0],
                title: singleNew[1],
                description: singleNew[2],
                image: singleNew[3],
                date: singleNew[4],
                author: singleNew[5],
            };

            setNews(formattedNew);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getNews();
    }, []);

    return (
        <div>
            <div>
                <div>
                    <div>{news && <UnvotedNew news={news} />}</div>
                    <div>
                        <Link to="/">
                            <button>
                                Go Back
                            </button>
                        </Link>
                        <select
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  className="dark:text-white mt-2 h-12 p-2 dark:border-gray-600 border rounded-xl border-borderWhiteGray bg-white dark:bg-backgroundBlack dark:text-[#9CA3AF] focus:outline-none"
                >
                  <option>Music</option>
                  <option>Sports</option>
                  <option>Gaming</option>
                  <option>News</option>
                  <option>Entertainment</option>
                  <option>Education</option>
                  <option>Technology</option>
                  <option>Travel</option>
                </select>
                    </div>
                </div>
            </div>
        </div>
    );
}
