import React, { useState, useRef } from "react";
import { create } from "ipfs-http-client";
import getContract from "./utilities/getContract";

export default function Writing() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");

  const client = create("https://ipfs.infura.io:5001/api/v0");
  const ImageRef = useRef();

  const handleSubmit = async () => {
    if (
      title === "" ||
      description === "" ||
      image === ""
    ) {
      alert("All the fields must be filled!");
      return;
    } else {
      uploadImage(image);
    }
  };

  const uploadImage = async (image) => {
    alert("Uploading Image...");

    try {
      const img = await client.add(image);
      await saveNew(img.path);

    } catch (err) {
      error("Error While Uploading Image");
    }
  };

  const saveNew = async (image) => {
    alert("Saving New...");

    console.log(title, description, image);

    try {
      const contract = await getContract();
      const UploadedDate = String(new Date());
      console.log(contract);

      await contract.createNew(
        title,
        description,
        image,
        UploadedDate
      );

      alert("New Saved Successfully");

      // reset form
      setTitle("");
      setDescription("");
      setImage("");

      // Redirect to Home Page
      window.location.href = "/";
    } catch (err) {
      console.log(err, "Error Saving Feed");
    }
  };

  // Handles redirect to Home Page or previous page
  const goBack = () => {
    window.history.back();
  };

  return (
    //return düzenle
    <div>
      <div>
        <div>
          <button
            onClick={() => {
              goBack();
            }}
          >
            Cancel
          </button>
          <button
            onClick={() => {
              handleSubmit();
            }}>
            <p>Publish</p>
          </button>
        </div>
      </div>
      <div>
        <div>
          <label>
            Title
          </label>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Write your title here!"
          />
          <label>
            Body
          </label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Write your article here!"
          />
            <label>
              Image
            </label>
            <div
              onClick={() => {
                ImageRef.current.click();
              }}
            >
              {image ? (
                <img
                  onClick={() => {
                    ImageRef.current.click();
                  }}
                  src={URL.createObjectURL(image)}
                  alt="image"
                />
              ) : (
                <div></div>
              )}
            </div>

            <input
              type="file"
              className="hidden"
              ref={ImageRef}
              onChange={(e) => {
                setImage(e.target.files[0]);
              }}
            />
          </div>
        </div>
      </div>
  );
}
