import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import NewsList from "../components/NewsList";
import getContract from "../utilities/getContract";
import logo from '../images/logo1.jpg'

function App() {
  const [news, setNews] = useState([]);
  const [currentAccount, setCurrentAccount] = useState("");

  const checkIfWalletIsConnected = async () => {
    try {
      const { ethereum } = window;

      const accounts = await ethereum.request({ method: "eth_accounts" });

      if (accounts.length !== 0) {
        const account = accounts[0];
        setCurrentAccount(account);
        alert("Wallet is Connected!");
      } else {
        alert("In order to read, write or vote a new, ensure your wallet is onnected!");
      }
    } catch (err) {
      console.log(`${err.message}`);
    }
  };

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Make sure you have MetaMask Connected");
        return;
      }

      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setCurrentAccount(accounts[0]);
    } catch (err) {
      console.log(`${err.message}`);
    }
  };

  const getNews = async () => {
    try {
      const contract = await getContract();
      const AllNews = await contract.getAllNews();
      let votedNews = [];
      for (let i = 0; i < AllNews.length; ++i) {
        if (AllNews[i].voted) {
          if (AllNews[i].approved == 2) {
            votedNews.push(AllNews[i]);
          }
        }
      }

      const formattedNew = votedNews.map((New) => {
        return {
          id: New.id,
          title: New.title,
          description: New.description,
          image: New.image,
          author: New.author,
          date: new Date(New.date * 1000),
        };
      });
      setNews(formattedNew);
    } catch (err) {
      console.log(`${err.message}`);
    }
  };

  useEffect(() => {
    checkIfWalletIsConnected();
    getNews();
  }, []);

  return (
    <div className='body' style={{ display: "flex", flexDirection: "column" }}>
      <div className='Header' style={{ display: "flex", flexDirection: "row", justifyContent: "space-between", alignItems: "center", backgroundColor: "black", padding: "11px" }}>
        <div>
          <Link to="/" style={{ textDecoration: "none", color: "black" }}>
            <img src={logo} height="100px" width="350px" alt='logo' />
          </Link>
        </div>

        {currentAccount ? (
          <div className='buttons'>
            <Link to="/writing">
              <button>
                <span>Write a New</span>
              </button>
            </Link>
            <Link to="/voting" style={{ marginLeft: "10px" }}>
              <button>
                <span>Vote a New</span>
              </button>
            </Link>
          </div>
        ) : (
          <div>
            <button
              onClick={() => {
                connectWallet();
              }}
            >
              <span>Connect your wallet</span>
            </button>
          </div>
        )}
      </div>

      <div className='NewsContainer' style={{ display: "flex", flexWrap: "wrap", padding: "11px" }}>
        {news.map((New, index) => {
          return (
            <Link to={`/New?id=${New.id}`} key={index} style={{ textDecoration: "none" }}>
              <div className='EveryNew' style={{ width: "min-content", height: "min-content", margin: "50px", marginLeft: "0px" }}>
                <NewsList New={New} />
              </div>
            </Link>
          );
        })}
      </div>

      <div className="footer" style={{
        backgroundColor: "black", position: "fixed",
        bottom: "0%",
        width: "100%"
      }}>
        <p style={{ color: 'white', textAlign: "center" }}> Copyright </p>
      </div>
    </div>
  );
}

export default App;
