import React from "react";

export default function NewsList({ New }) {
  let url = `https://ipfs.infura.io/ipfs/${New.image}`;
  return (
    <div style={{ 
      backgroundImage: `url("https://ipfs.infura.io/ipfs/${New.image}")`, height:"250px", width:"250px" }}>
      <div>
        <h4 style={{color:"white"}}>
          {New.title}
        </h4>
          <p style={{color:"white"}}>
            {New.description.slice(0, 50)}...
          </p>
      </div>
    </div>
  );
}
