import React from "react";

export default function NewsList({ New }) {
  return (
    <div style={{ 
      backgroundImage: `url(${`https://ipfs.infura.io/ipfs/${New.image}`})` }}>
      <img 
        width={250} height={250}
        alt="image"
      />
      <div>
        <h4 style={{color:"white"}}>
          {New.title}
        </h4>
          <p style={{color:"white"}}>
            {New.description.slice(0, 50)}...
          </p>
      </div>
    </div>
  );
}
