import React from "react";

export default function NewsList({ New }) {
  return (
    <div>
      <div style={{ 
      backgroundImage: `url("https://ipfs.infura.io/ipfs/${New.image}")`, backgroundSize: "cover", height:"250px", width:"250px", justifyContent:"center"}}>
        <h4 style={{color:"white"}}>
          {New.title}
        </h4>
      </div>
      <p style={{color:"black"}}>
          <b>{New.description.slice(0, 50)}...</b>
      </p>
    </div>
  );
}
